package io.mda.fields;import android.support.design.widget.TextInputLayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.AppCompatEditText;import android.view.View;import android.widget.Button;public class MainActivity extends AppCompatActivity {    private AppCompatEditText editTextEmail;    private AppCompatEditText editTextPassword;    private TextInputLayout textInputLayoutEmail;    private TextInputLayout textInputLayoutPassword;    private Button btnLogin;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        editTextEmail = (AppCompatEditText) findViewById(R.id.email);        editTextPassword = (AppCompatEditText) findViewById(R.id.senha);        textInputLayoutEmail = (TextInputLayout) findViewById(R.id.txtEmail);        textInputLayoutPassword = (TextInputLayout) findViewById(R.id.txtSenha);        btnLogin = (Button) findViewById(R.id.btnLogin);        btnLogin.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                validateForm();            }        });    }    private void validateForm(){        if(editTextEmail.getText().toString().isEmpty()){            textInputLayoutEmail.setErrorEnabled(true);            textInputLayoutEmail.setError("Preencha com seu email");        }else{            textInputLayoutEmail.setErrorEnabled(false);        }        if(editTextPassword.getText().toString().isEmpty()){            textInputLayoutPassword.setErrorEnabled(true);            textInputLayoutPassword.setError("Preencha com sua Senha");        }else{            textInputLayoutPassword.setErrorEnabled(false);        }    }}