package io.android.firebise;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;public class MainActivity extends AppCompatActivity {    private DatabaseReference f = FirebaseDatabase.getInstance().getReference();    private DatabaseReference db = f.child("usuario");    private EditText dados;    private Button btn;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        dados = (EditText) findViewById(R.id.edt);        btn = (Button) findViewById(R.id.btn);        final Usuario u = new Usuario();        u.setNome("Rafael");        u.setSobreNome("Carvalho");        u.setIdade(31);        btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                if(dados != null){                    String info = dados.toString();                    db.child("001").setValue(u);                }            }        });    }}