package io.android.firebise;/** * Created by rafaelcarvalho on 15/12/2017. */public class Produto {    private String produto;    private String cor;    private String fabricante;    public Produto() {    }    public String getProduto() {        return produto;    }    public void setProduto(String produto) {        this.produto = produto;    }    public String getCor() {        return cor;    }    public void setCor(String cor) {        this.cor = cor;    }    public String getFabricante() {        return fabricante;    }    public void setFabricante(String fabricante) {        this.fabricante = fabricante;    }}