package io.android.firebise;/** * Created by rafaelcarvalho on 15/12/2017. */public class Usuario {    private String nome;    private String sobreNome;    private int idade;    public Usuario() {    }    public String getNome() {        return nome;    }    public String getSobreNome() {        return sobreNome;    }    public void setNome(String nome) {        this.nome = nome;    }    public void setSobreNome(String sobreNome) {        this.sobreNome = sobreNome;    }    public void setIdade(int idade) {        this.idade = idade;    }    public int getIdade() {        return idade;    }}